<?		session_start();	if(isset($_SESSION["login_usuario_logado"]))		$login_usuario = $_SESSION["login_usuario_logado"];			if(isset($_SESSION["senha_usuario_logado"]))		$senha_usuario = $_SESSION["senha_usuario_logado"];		if(!(empty($login_usuario) OR empty($senha_usuario))) {		$usuario = new Usuario;		$usuario			->where("login='".$login_usuario."' and senha='".$senha_usuario."'")			->find();		$usuarios = $usuario->allToArray();				if(count($usuarios) == 1) {			if($senha_usuario != $usuarios['0']['senha']) {				unset($_SESSION["login_usuario_logado"]);				unset($_SESSION["senha_usuario_logado"]);				echo "<meta http-equiv=\"Refresh\" content=\"0;URL=http://".$_SERVER['HTTP_HOST']."/login.php\">";				//exit("É necessário estar logado para acessar essa página.");				exit;			}		}		else {			unset ($_SESSION["login_usuario_logado"]);			unset ($_SESSION["senha_usuario_logado"]);			echo "<meta http-equiv=\"Refresh\" content=\"0;URL=http://".$_SERVER['HTTP_HOST']."/login.php\">";			//exit("É necessário estar logado para acessar essa página.");			exit;		}	}	else {		echo "<meta http-equiv=\"Refresh\" content=\"0;URL=http://".$_SERVER['HTTP_HOST']."/login.php\">";		//exit("É necessário estar logado para acessar essa página.");		exit;	}?>